KUBE_CONFIG=/etc/kubernetes/admin.conf
AWSED_
K8S_ENDPOINT=`sudo sed -nr 's/^\W*server:\W(.*)$/\1/p' $KUBE_CONFIG`
K8S_CA=`sudo sed -nr 's/^\W*certificate-authority-data:\W(.*)$/\1/p' $KUBE_CONFIG`
K8S_CERT=`sudo sed -nr 's/^\W*client-certificate-data:\W(.*)$/\1/p' $KUBE_CONFIG`
K8S_KEY=`sudo sed -nr 's/^\W*client-key-data:\W(.*)$/\1/p' $KUBE_CONFIG`

echo AWSED_CA: $AWSED_CA
echo K8S_ENDPOINT: $K8S_ENDPOINT
echo K8S_CA: $K8S_CA
echo K8S_CERT: $K8S_CERT
echo K8S_KEY: $K8S_KEY
